import axios from "axios";

export class TarefasService {
    static serverURL = 'http://localhost:8000/api';

    static getAllTarefas() {
        let dataURL = `${this.serverURL}/tarefas`;
        return axios.get(dataURL, { headers: { 'Content-Type': 'application/json' } });
    }

    static getTarefa(tarefaId) {
        let dataURL = `${this.serverURL}/lista/${tarefaId}`;
        return axios.get(dataURL, { headers: { 'Content-Type': 'application/json' } });
    }

    static criarTarefa(tarefa) {
        let dataURL = `${this.serverURL}/tarefas`;
        return axios.post(dataURL, tarefa, { headers: { 'Content-Type': 'application/json' } });
    }

    static atualizarTarefa(tarefa, tarefaId) {
        let dataURL = `${this.serverURL}/editar/${tarefaId}`;
        return axios.put(dataURL, tarefa, { headers: { 'Content-Type': 'application/json' } });
    }

    static finalizarTarefa(tarefa, tarefaId) {
        let dataURL = `${this.serverURL}/update/${tarefaId}`;
        return axios.put(dataURL, tarefa, { headers: { 'Content-Type': 'application/json' } });
    }

    static deletarTarefa(tarefaId) {
        let dataURL = `${this.serverURL}/delete/${tarefaId}`;
        return axios.delete(dataURL, { headers: { 'Content-Type': 'application/json' } });
    }
}
